/*License MIT (c) 2015 Samantha-Mazzei <http://samantha-mazzei.com>*/$(document).ready(function(){displayClock();setInterval(displayClock,1000);});function displayClock(){var today=new Date();var hours=today.getHours();var minutes=today.getMinutes();var seconds=today.getSeconds();if(hours<10){hours='0'+hours;} if(minutes<10){minutes='0'+minutes;} if(seconds<10){seconds='0'+seconds;} $('.clock').html(hours+':'+minutes+':'+seconds);}